bool init(){
	//Initialization flag
	bool success = true;

	//Initialize SDL
	if( SDL_Init( SDL_INIT_VIDEO ) < 0 ){
		printf( "SDL could not initialize! SDL Error: %s\n", SDL_GetError() );
		success = false;
	}
	else{
		//Set texture filtering to linear
		if( !SDL_SetHint( SDL_HINT_RENDER_SCALE_QUALITY, "1" ) ){
			printf( "Warning: Linear texture filtering not enabled!" );
		}

		//Create window
		gWindow = SDL_CreateWindow( "SDL Tutorial", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN );
		if( gWindow == NULL ){
			printf( "Window could not be created! SDL Error: %s\n", SDL_GetError() );
			success = false;
		}
		else{
			//Create vsynced renderer for window
			gRenderer = SDL_CreateRenderer( gWindow, -1, SDL_RENDERER_ACCELERATED | SDL_RENDERER_PRESENTVSYNC );
			if( gRenderer == NULL ){
				printf( "Renderer could not be created! SDL Error: %s\n", SDL_GetError() );
				success = false;
			}
			else{
				//Initialize renderer color
				SDL_SetRenderDrawColor( gRenderer, 0xFF, 0xFF, 0xFF, 0xFF );

				//Initialize PNG loading
				int imgFlags = IMG_INIT_PNG;
				if( !( IMG_Init( imgFlags ) & imgFlags ) )
				{
					printf( "SDL_image could not initialize! SDL_image Error: %s\n", IMG_GetError() );
					success = false;
				}
			}
		}
	}

	return success;
}

bool loadMedia(){
	//Loading success flag
	bool success = true;

	if( !gPlayer1Texture.loadFromFile(gRenderer, "Resources/NEWDOT.BMP" ) ){
		printf( "Failed to load dot texture!\n" );
		success = false;
	}
	if( !gPlayer2Texture.loadFromFile(gRenderer, "Resources/NEWDOT.BMP" ) ){
		printf( "Failed to load dot texture!\n" );
		success = false;
	}
	if( !gBackgroundTexture.loadFromFile(gRenderer, "Resources/backg.png" ) ){
		printf( "Failed to load background texture image!\n" );
		success = false;
	}
	if( !gRoadTexture.loadFromFile(gRenderer, "Resources/smallroad.png" ) ){
		printf( "Failed to load tile texture image!\n" );
		success = false;
	}
	if( !gBuildingTexture.loadFromFile(gRenderer, "Resources/build.png" ) ){
		printf( "Failed to load tile texture image!\n" );
		success = false;
	}
	if( !gGrassTexture.loadFromFile(gRenderer, "Resources/smallgrass.png" ) ){
		printf( "Failed to load tile texture image!\n" );
		success = false;
	}
	if(!gGuardTexture.loadFromFile(gRenderer, "Resources/pi.png")){
		printf("Failed to load guard texture image!\n");
		success = false;
	}
	return success;
}

void close(){
	//Free loaded images
	gPlayer1Texture.free();
	gPlayer2Texture.free();
	gBackgroundTexture.free();
	gRoadTexture.free();
	gBuildingTexture.free();
	gGrassTexture.free();
	gGuardTexture.free();

	//Destroy window	
	SDL_DestroyRenderer( gRenderer );
	SDL_DestroyWindow( gWindow );
	gWindow = NULL;
	gRenderer = NULL;

	//Quit SDL subsystems
	IMG_Quit();
	SDL_Quit();
}

int main( int argc, char* args[] ){
	//Start up SDL and create window

	if( !init() ){
		printf( "Failed to initialize!\n" );
	}
	else{
		//Load media
		if( !loadMedia() ){
			printf( "Failed to load media!\n" );
		}
		else{
			//Main loop flag
			bool quit = false;
			Uint32 startTime = 0;

			//Event handler
			SDL_Event e;

			//The dot that will be moving around on the screen
			Player player1(72, 300);
			Player player2(1032, 350);
			Guard guard1(72, 325);
			//Guard guard2(1152, 434);
			//Guard guard3(660, 648);
			//Guard guard4(660, 156);
			//Guard guard5(348, 72);

			//While application is running
			while( !quit ){
				//Handle events on queue
				while( SDL_PollEvent( &e ) != 0 ){
					//User requests quit
					if( e.type == SDL_QUIT ){
						quit = true;
					}

					//Handle input for the dot
					player1.handleEvent( e );
					player2.handleEvent( e );
					
				//guard5.update(player1 , player2, map);
				}

				//Move the dot
				player1.movePlayer(map);
				player2.movePlayer(map);
				guard1.moveGuard(player1 , player2, map);
				//guard2.moveGuard(player1 , player2, map);
				//guard3.moveGuard(player1 , player2, map);
				//guard4.moveGuard(player1 , player2, map);
				

				//Clear screen
				SDL_SetRenderDrawColor( gRenderer, 0xFF, 0xFF, 0xFF, 0xFF );
				SDL_RenderClear( gRenderer );

				//Render objects
				
				for (int i = 0 ; i < 70 ; i++){
					for (int j = 0 ; j < 100 ; j ++){
						if (map[i][j] == 0){
							gBackgroundTexture.render(gRenderer, TILE_SIZE*j,TILE_SIZE*i);
						}
                        else if(map[i][j] == 1){
							gRoadTexture.render(gRenderer, TILE_SIZE*j,TILE_SIZE*i);
						}else if(map[i][j] == 2){
							gGrassTexture.render(gRenderer, TILE_SIZE*j,TILE_SIZE*i);
						}else if(map[i][j] == 3){
							gBuildingTexture.render(gRenderer, TILE_SIZE*j,TILE_SIZE*i);
						}
					}
				}	
			
				player1.renderPlayer(gRenderer, gPlayer1Texture);
				player2.renderPlayer(gRenderer, gPlayer2Texture);
				guard1.renderGuard(gRenderer, gGuardTexture);
				//guard2.renderGuard(gRenderer, gGuardTexture);
				//guard3.renderGuard(gRenderer, gGuardTexture);
				//guard4.renderGuard(gRenderer, gGuardTexture);
				//guard5.renderGuard(gRenderer, gGuardTexture);

				//Update screen
				SDL_RenderPresent( gRenderer );

				
			}
		}
	}

	//Free resources and close SDL
	close();

	return 0;
}
